mapper.insert:
INSERT INTO user (id, name, password, role, status, use_yn, creator_id, modifier_id, created_at, modified_at)
SELECT * FROM (SELECT #{id} AS id, #{name} as NAME, #{password} AS password,'COUNSELOR', #{role} AS role, "impossible" AS status, 'y' AS use_yn, #{creator_id} AS creator_id, #{modifier_id} AS modifier_id, now() AS created_at,now() AS modified_at) AS tmp
WHERE NOT EXISTS (
SELECT id FROM user WHERE id = #{id} AND role = 'COUNSELOR'
) LIMIT 1;

INSERT INTO charger (category_id, user_id, creator_id, created_at)
SELECT * FROM (SELECT 0 AS category_id, #{id} AS user_id, #{creator_id} AS creator_id, now() AS created_at) AS tmp
WHERE NOT EXISTS (
SELECT user_id FROM charger WHERE user_id = #{id}
) LIMIT 1;
