mapper.insert:
INSERT INTO user (id, name, password, role, created_at, modified_at)
SELECT * FROM (SELECT #{userId} AS id, #{name} as NAME, #{password} AS password,'COUNSELOR', #{role} AS role,now() AS created_at,now() AS modified_at) AS tmp
WHERE NOT EXISTS (
SELECT id FROM user WHERE id = #{userId} AND role = 'COUNSELOR'
) LIMIT 1;

INSERT INTO charger (user_id, created_at)
SELECT * FROM (SELECT #{userId} AS user_id, now() AS created_at) AS tmp
WHERE NOT EXISTS (
SELECT user_id FROM charger WHERE user_id = #{userId}
) LIMIT 1;
