mapper.select:
SELECT EXISTS
    (
        SELECT id
        FROM user
        WHERE status = "POSSIBLE"
    );

SELECT role
FROM user
WHERE id = #{id}

SELECT EXISTS
    (
        SELECT id
        FROM user
        WHERE id = #{id}
    );

SELECT EXISTS
    (
        SELECT id
        FROM user
        WHERE id = #{id} and password = #{password}
    );

mapper.update:
UPDATE user
SET
    status = #{userStatus}, modified_at = NOW(), modifier_id = #{id}
WHERE
    id = #{id};
